import React, { useState } from 'react'
import {ProgressBar} from 'react-bootstrap'
import styled from 'styled-components';
import './App.css'
import './assets/img/capybara.png'

import { Jutsu } from 'react-jutsu'

const App = () => {
  const [room, setRoom] = useState('')
  const [name, setName] = useState('')
  const [call, setCall] = useState(false)

  const handleClick = event => {
    event.preventDefault()
    if (room && name) setCall(true)
  }

  const Title = styled.h1`
    font-size: 2em;
    text-align: left;
    color: darkcyan;
    text-align: center;
    letter-spacing: 2px;
    background: url(assets/img/chalkboard-background.jpg);
  `;

  const Wrapper = styled.section`
    padding: 2%;
    background: black;
    opacity: 0.65;
    background-image: url(./insertion-header.jpg);
  `;

  const Background = styled.section`
    background: peachpuff;
    width: 50%;
    height: 50%;
    border: 2px solid moccasin;
  `;

  const Button = styled.button`
    font-size: 1em;
    font-family: verdana;
    margin: 1em;
    padding: 0.5em 1em;
    border: 
    border: 2px solid rgb(39, 40, 39);
    border-radius: 5px;
  `;

  return call ? (
      <>
      <Wrapper>
        <section className="section-header">
            <h1 className="header">Bienvenidx a aula virtual!</h1>
        </section>
      </Wrapper>
      <Jutsu
        roomName={room}
        userName={name}
        loadingComponent={<ProgressBar now={60}/>}
        containerStyles={{ width: '100%', height: '700px' }}
      />
      </>
  ) : (
    <div className="todo">
      <section className="section-header">
          <h1 className="header">AULA VIRTUAL</h1>
          <img src='https://png.pngtree.com/png-clipart/20200225/original/pngtree-image-of-capybara-vector-or-color-illustration-png-image_5274642.jpg'></img>
      </section>
      <section className="form">
        <input id='room' type='text' placeholder='Sala' value={room} onChange={(e) => setRoom(e.target.value)} />
        <input id='name' type='text' placeholder='Nombre' value={name} onChange={(e) => setName(e.target.value)} />
        <Button onClick={handleClick} type='submit'>
          Iniciar
        </Button>
      </section>
      <footer className="footer">Ac√° va un footer</footer>
    </div>
  )
}

export default App
