import React, { useState } from 'react'
import {ProgressBar} from 'react-bootstrap'
import {Spinner} from 'react-bootstrap'
import styled from 'styled-components';

import { Jutsu } from 'react-jutsu'

const App = () => {
  const [room, setRoom] = useState('')
  const [name, setName] = useState('')
  const [call, setCall] = useState(false)

  const handleClick = event => {
    event.preventDefault()
    if (room && name) setCall(true)
  }

  const Title = styled.h1`
    font-family: verdana;
    font-size: 1.5em;
    text-align: left;
    color: darkcyan;
  `;

  const Wrapper = styled.section`
    padding: 2%;
    background: black;
    opacity: 0.65;
  `;

  const Background = styled.section`
    display: flex;
    background: peachpuff;
  `;

  const Button = styled.button`
    font-size: 1em;
    font-family: verdana;
    margin: 1em;
    padding: 0.5em 1em;
    border: 2px solid darkcyan;
    border-radius: 5px;
  `;

  const Input = styled.input`
    font-size: 1em;
    margin: 1em;
    padding: 0.5em 1em;
    border: 2px solid darkcyan;
    border-radius: 3px;
  `;

  return call ? (
      <>
      <Wrapper>
        <Title>Bienvenidx a aula virtual!</Title>
      </Wrapper>
      <Jutsu
        roomName={room}
        userName={name}
        loadingComponent={<ProgressBar now={60}/>}
        containerStyles={{ width: '100%', height: '700px' }}
      />
      </>
  ) : (
    <form>
      <Wrapper>
        <Title>Aula virtual</Title>
      </Wrapper>
      <Background>
      <Input id='room' type='text' placeholder='Sala' value={room} onChange={(e) => setRoom(e.target.value)} />
      <Input id='name' type='text' placeholder='Nombre' value={name} onChange={(e) => setName(e.target.value)} />
      <Button onClick={handleClick} type='submit'>
        Iniciar
      </Button>
      </Background>
    </form>
  )
}

export default App
