import React, { useState } from 'react'
import {ProgressBar} from 'react-bootstrap'
import styled from 'styled-components';
import './App.css'

import { Jutsu } from 'react-jutsu'

const App = () => {
  const [room, setRoom] = useState('')
  const [name, setName] = useState('')
  const [call, setCall] = useState(false)

  const handleClick = event => {
    event.preventDefault()
    if (room && name) setCall(true)
  }

  const Title = styled.h1`
    font-size: 2em;
    text-align: left;
    color: darkcyan;
    text-align: center;
    letter-spacing: 2px;
  `;

  const Wrapper = styled.section`
    padding: 2%;
    background: black;
    opacity: 0.65;
    background-image: url(./insertion-header.jpg);
  `;

  const Background = styled.section`
    background: peachpuff;
    width: 50%;
    height: 50%;
    border: 2px solid moccasin;
  `;

  const Button = styled.button`
    font-size: 1em;
    font-family: verdana;
    margin: 1em;
    padding: 0.5em 1em;
    border: 2px solid darkcyan;
    border-radius: 5px;
  `;

  const Input = styled.input`
    font-size: 1em;
    margin: 1em;
    padding: 0.5em 1em;
    border: 2px solid darkcyan;
    border-radius: 3px;
  `;

  const Image = styled.input`
    display: block;
    margin: 0 auto;
    src={imgSource};
  `;

  return call ? (
      <>
      <Wrapper>
        <Title>Bienvenidx a aula virtual!</Title>
      </Wrapper>
      <Jutsu
        roomName={room}
        userName={name}
        loadingComponent={<ProgressBar now={60}/>}
        containerStyles={{ width: '100%', height: '700px' }}
      />
      </>
  ) : (
    <body>
    <div>
    <section className="section-header">
        <h1 className="header">Aula Virtual</h1>
      </section>
    <section className="form">
      <Input id='room' type='text' placeholder='Sala' value={room} onChange={(e) => setRoom(e.target.value)} />
      <Input id='name' type='text' placeholder='Nombre' value={name} onChange={(e) => setName(e.target.value)} />
      <Button onClick={handleClick} type='submit'>
        Iniciar
      </Button>
    </section>
    <footer className="footer">Created by <a href="http://unlam.edu.ar">Unlam</a> Â© 2020</footer>
    </div>
    </body>
  )
}

export default App
